
CREATE TABLE `Metronic_task` (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `days` SMALLINT(4) UNSIGNED NOT NULL COMMENT '宝宝天数',
  `wife_title` VARCHAR(50) NOT NULL COMMENT '任务标题',
  `wife_content` VARCHAR(500) NOT NULL COMMENT '任务内容',
  `husband_title` VARCHAR(50) NOT NULL COMMENT '任务标题',
  `husband_content` VARCHAR(500) NOT NULL COMMENT '任务内容',
  `status` TINYINT(1) NOT NULL DEFAULT '0' COMMENT '状态[0=禁用,1=启用]',
  `orderby` INT(10) UNSIGNED NOT NULL COMMENT '排序',
  `dateline` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `days` (`days`)
) ENGINE =MyISAM AUTO_INCREMENT =1 DEFAULT CHARSET =utf8 COMMENT ='孕妈准爸任务';















