CREATE TABLE `pt_grow_links` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `week` smallint(4) unsigned NOT NULL COMMENT '宝宝周数',
  `title` varchar(30) NOT NULL COMMENT '标题',
  `url` varchar(200) NOT NULL COMMENT '链接',
  `status` tinyint(1) unsigned NOT NULL default '0' COMMENT '状态[0=隐藏,1=显示]',
  `orderby` int(10) unsigned NOT NULL default '0' COMMENT '排序',
  PRIMARY KEY  (`id`),
  KEY `week` (`week`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='育儿链接';

