DROP TABLE IF EXISTS `pt_nutrition`;
CREATE TABLE `pt_nutrition` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `type_id` int(3) NOT NULL COMMENT '分类ID',
  `title` varchar(30) NOT NULL COMMENT '标题',
  `content` varchar(500) NOT NULL COMMENT '正文',
  `url` varchar(200) NOT NULL COMMENT '链接',
  `hits` int(11) NOT NULL COMMENT '点赞数',
  `month` smallint(4) unsigned NOT NULL COMMENT '内容月份标识',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态[0=隐藏,1=显示]',
  `orderby` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=76 DEFAULT CHARSET=utf8 COMMENT='喂养';

