INSERT INTO `pt_grow_tips` (`id`, `week`, `titletype`, `title`, `content`, `status`, `orderby`) VALUES
(1, 1, 1, '宝宝第1周发育规律', '出生第一周时宝宝体重大多数会暂时下降，这是因为宝宝这时吃奶少，加上胎便和尿液的排出以及皮肤出汗和呼吸使一部份身体水分损失，在出生后2-4天会出现暂时性体重下降的现象，医学上称为“生理性体重下降”一般下降不超过300克。随着吃奶量的增加，宝宝的体重从第4-5天开始回升，这一周即可恢复到出生时的体重。', 1, 1),
(2, 1, 2, '视觉', '“看见”不等于“看清”新生宝宝到了第二周，视力就会有个小突破，他可以看见3米远的位置啦，能够跟踪到3米内的运动物体，但是能够看清楚的距离则只有15cm哦。不过这样也已经很棒了！这个时候开始你的宝宝已经有了自己的小喜好，颜色对比反差大的物体会更容易获得他们的青睐。这是因为对于视力还非常微弱的宝宝，色彩对比越明显，他能够catch到的可能性就越大，看来对成就感的渴望从新生儿时期就已经存在着啦！', 1, 2),
(3, 1, 3, '听觉', '婴儿突然听到大声响时会眨眼睛 0-3个月为宝宝听性反射的阶段，这时宝宝的大脑还没有完全发育。婴儿如果突然听到大声响，如大声的喊叫声或击掌声时，就会出现惊跳反射、吸吮反射、睁眼反射、闭眼反射等；有的还可能因为受到惊吓而哭闹。', 1, 3),
(4, 1, 4, '语言', '宝宝会用哭来表示需求第二周，宝宝开始喜欢听到爸爸妈妈和他说话，开始与人“交谈”。也会用哭声表示需求，发出细小的喉音。除因尿湿、饥饿、不适等哭闹外，也会因希望被陪伴和关注而啼哭。', 1, 4),
(5, 1, 5, '动作', '别会错意哦 宝宝的笑还是无意义的一周后，宝宝会四肢会伸展，出现一些特定的肢体反射运动，手有抓握的能力。如果你把手指放入小宝贝的手掌中，他会立即有回握反应哦。宝宝可能会在睡眠中、或者在一些时候冲你微笑，但别臭美啦，这个时候宝宝的笑还是无意义的。', 1, 5),
(6, 2, 1, '宝宝第2周发育规律', '出生第一周时宝宝体重大多数会暂时下降，这是因为宝宝这时吃奶少，加上胎便和尿液的排出以及皮肤出汗和呼吸使一部份身体水分损失，在出生后2-4天会出现暂时性体重下降的现象，医学上称为“生理性体重下降”一般下降不超过300克。随着吃奶量的增加，宝宝的体重从第4-5天开始回升，这一周即可恢复到出生时的体重.', 1, 1),
(7, 2, 2, '视觉', '“看见”不等于“看清”新生宝宝到了第二周，视力就会有个小突破，他可以看见3米远的位置啦，能够跟踪到3米内的运动物体，但是能够看清楚的距离则只有15cm哦。不过这样也已经很棒了！这个时候开始你的宝宝已经有了自己的小喜好，颜色对比反差大的物体会更容易获得他们的青睐。这是因为对于视力还非常微弱的宝宝，色彩对比越明显，他能够catch到的可能性就越大，看来对成就感的渴望从新生儿时期就已经存在着啦!', 1, 2),
(8, 52, 1, '宝宝第52周发育规律', '出生第52周时宝宝体重大多数会暂时下降，这是因为宝宝这时吃奶少，加上胎便和尿液的排出以及皮肤出汗和呼吸使一部份身体水分损失，在出生后2-4天会出现暂时性体重下降的现象，医学上称为“生理性体重下降”一般下降不超过300克。随着吃奶量的增加，宝宝的体重从第4-5天开始回升，这一周即可恢复到出生时的体重', 1, 1),
(9, 52, 2, '听觉', '婴儿突然听到大声响时会眨眼睛 0-3个月为宝宝听性反射的阶段，这时宝宝的大脑还没有完全发育。婴儿如果突然听到大声响，如大声的喊叫声或击掌声时，就会出现惊跳反射、吸吮反射、睁眼反射、闭眼反射等；有的还可能因为受到惊吓而哭闹。[br]', 1, 2);
